import home from "@/views/home"
import {resolve} from "@babel/core/lib/vendor/import-meta-resolve";

/**
 * 格式化菜单 将菜单转换为主键
 * @param {array} menus 传入的菜单信息
 */
export const formatMenu = (menus) => {
  // 迭代菜单
  const route = menus.filter(item => {
    item.component = (item.component === "home") ? home : loadView(item.component);
    // 处理子菜单
    if (item.childrenMenu && item.childrenMenu.length > 0) {
      formatMenu(item.childrenMenu);
    }
    return true;
  });
  // 将格式化后的信息进行返回
}

/**
 * 路由懒加载
 * @param view
 * @returns {function(*): any}
 */
const loadView = (view) => {
  return (resolve) => require(["@views/${view}.vue"], resolve);
}
