import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    token: sessionStorage.getItem("token") || '',
    name: sessionStorage.getItem("token") || '',
    avatar: sessionStorage.getItem("token") || '',
    roles: [],
  },
  getters: {},
  mutations: {
    setToken(state, data) {
      state.token = data;
      sessionStorage.setItem("token", data);
    },
    setName(state, data) {
      state.name = data;
      sessionStorage.setItem("name", data);
    },
    setAvatar(state, data) {
      state.avatar = data;
      sessionStorage.setItem("avatar", data);
    },
    setRoles(state, data) {
      state.roles = data;
      sessionStorage.setItem("roles", data);
    },
  },
  actions: {},
  modules: {},
});
