import Vue from "vue";
import VueRouter from "vue-router";
import Login from "@/views/login";
import Home from  "@/views/home"

Vue.use(VueRouter);

const routes = [
  {
    path: "/login",
    name: "login",
    component: Login,
  },
  {
    path: "/home",
    name: "home",
    component: Home,
  }
];

const router = new VueRouter({
  routes,
});

/**
 * 路由导航
 * to； 将要去哪个页面
 * from：从哪个页面过来
 * next: 放行到哪个页面
 */
router.beforeEach((to, from, next) => {
  // 用户是否登录
  const token = sessionStorage.getItem("token");
  if (!token) {
    if (to.path === "/login") {
      next();
    }
    next("/login");
  } else {
    next();
  }
});

export default router;
